import React from "react";
import {Link,Redirect} from 'react-router-dom';
import axios from 'axios';
import TextField from "material-ui/TextField";
import MuiThemeProvider from "material-ui/styles/MuiThemeProvider";
import RaisedButton from "material-ui/RaisedButton";

const styles = {
    button: {
        margin: 15,
    },
};

export class VerifyEmail extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
            emailVerified: false,
            authenticated:true,
            email:"",
            backendOTP:"0",
            frontendOTP:"",
        }
    }

    changeHandler = (e)=>{
        this.setState({
            [e.target.id]: e.target.value
        })
    }

    submitHanler = (e)=>{        
        let data={}
        data.frontendOTP = this.state.frontendOTP
        data.resend = false
        console.log(data)

        let authAxios=axios.create({
            headers: {
                Authorization: `Token ${localStorage.getItem('token')}`
            }
        });
        authAxios.post('http://localhost:8000/api/default/',data)
        .then(
            (response)=>{
                this.setState({
                    backendOTP: response.data.backendOTP,
                })
                console.log(response)
            }
        )
        .catch(
            (error)=>{
                this.setState({error: error.response})
                console.log(this.state.error)
            }
        )
    }

    resendOTP = (e)=>{        
        let data={}
        data.resend = true
        console.log(data)

        let authAxios=axios.create({
            headers: {
                Authorization: `Token ${localStorage.getItem('token')}`
            }
        });
        authAxios.post('http://localhost:8000/api/default/',data)
        .then(
            (response)=>{
                this.setState({
                    backendOTP: response.data.backendOTP,
                })
                console.log(response)
            }
        )
        .catch(
            (error)=>{
                this.setState({error: error.response})
                console.log(this.state.error)
            }
        )
    }

    componentDidMount() {
        let authAxios=axios.create({
            headers: {
                Authorization: `Token ${localStorage.getItem('token')}`
            }
        });
        authAxios.get('http://localhost:8000/api/default/')
        .then(
            (response)=>{
                this.setState({
                    email:response.data.email,
                    authenticated: true,
                    emailVerified: response.data.email_verified,
                    backendOTP: response.data.backendOTP,
                })
                console.log(this.state.email)
                console.log(this.state.otp)
                console.log(this.state.emailVerified)
            }
        )
        .catch(
            (error)=>{
                this.setState({
                    error:error.response,
                })
                console.log(this.state.error)
                if(this.state.error.status===423){
                    this.setState({emailVerified:true})
                }
                if(this.state.error.status===401){
                    this.setState({authenticated:false})
                }
            }
        )
    }

    render() {
        let {min,sec,emailVerified,authenticated,email,backendOTP,frontendOTP} = this.state;

        if(!authenticated){
            return(<Redirect to="/login" />)
        }
        if(emailVerified){
            return(<Redirect to="/profile" />)
        }

        return (
            <MuiThemeProvider>
                <React.Fragment>
                    <br/><br/>
                    <h2>User Verification</h2>
                    <h5>{email}</h5>
                    <br/><br/>
                    <TextField
                        hintText="6 digit otp"
                        floatingLabelText="6 Digit OTP"
                        id="frontendOTP"
                        value={frontendOTP}
                        onChange={this.changeHandler}
                    />
                    <RaisedButton
                        label="verify"
                        primary={true}
                        style={styles.button}
                        onClick={this.submitHanler}
                        type="submit"

                    />
                    <br/><br/>
                    <RaisedButton
                        label="Send OTP" 
                        primary={true}
                        style={styles.button}
                        onClick={this.resendOTP}
                        type="submit"
                    />
                </React.Fragment>
            </MuiThemeProvider>
        );
    }
}

export default VerifyEmail



