import React from "react";
import TextField from "material-ui/TextField";
import MuiThemeProvider from "material-ui/styles/MuiThemeProvider";
import RaisedButton from "material-ui/RaisedButton";

export class VerifyEmail extends React.Component {
    constructor(props) {
        super(props)

        this.state = {
        emailVerified: false,
        }
    }

    componentDidMount() {
        this.myInterval = setInterval(() => {
            const { min, sec } = this.state;

            if (sec > 0) {
                this.setState(({ sec }) => ({
                sec: sec - 1,
                }));
            }
            if (sec === 0) {
                if (min === 0) {
                clearInterval(this.myInterval);
                } else {
                    this.setState(({ min }) => ({
                        min: min - 1,
                        sec: 59,
                    }));
                }
            }   
        }, 1000);

        let authAxios=axios.create({
            headers: {
                Authorization: `Token ${localStorage.getItem('token')}`
            }
        });
        authAxios.get('http://localhost:8000/api/')
        .then(
            (response)=>{
                this.setState({
                    context:response.data,
                    authenticated: true,
                    emailVerified: response.data.email_verified
                })
                console.log(this.state.context)
            }
        )
        .catch(
            (error)=>{
                this.setState({
                    error:error.response,
                    authenticated: false
                })
                console.log(this.state.error)
            }
        )

  }

  componentWillUnmount() {
    clearInterval(this.myInterval);
  }

  render() {
    const { min, sec } = this.state;
    // const { checkMail, handleChange } = this.props;
    return (
        <MuiThemeProvider>
            <React.Fragment>
                <br /><br /><br />
                <h2>User Verification</h2>
                <br /><br /><br />

                <TextField
                    hintText="6 digit otp"
                    floatingLabelText="6 Digit OTP"
                  //  onChange={handleChange("otpSent")}
                />
                <RaisedButton
                    label="Check email"
                    primary="true"
                 //   onClick={checkMail}
                />
                <br/><br/>
                <RaisedButton label="Resend OTP" primary="true" disabled={false}/>
                <h5>
                    <br/>Resend OTP in {min}:{sec}
                </h5>
            </React.Fragment>
        </MuiThemeProvider>
    );
  }
}

export default VerifyEmail
