import React from 'react'
import MuiThemeProvider from "material-ui/styles/MuiThemeProvider";
import TextField from "material-ui/TextField";
import RaisedButton from "material-ui/RaisedButton";
import {Link,Redirect} from 'react-router-dom';

const styles = {
	button: {
		margin: 15,
    },
};

export class Login extends React.Component {
	constructor(props) {
		super(props)

		this.state = {
			 context: {},
			 email: "",
			 password: ""
		}
	}

	changeHandler = (e)=>{
		this.setState({
			[e.target.id]: e.target.value
		})
	}

	render() {
		let {email,password} = this.state
		return (
			<MuiThemeProvider>
				<React.Fragment>
					<br/>
					<Link to="/login">
						<RaisedButton	
							label="log-In"
							primary={false} 
							style={styles.button} 
						/>
					</Link>
					<Link to="/signup">
						<RaisedButton
				            label="Sign-Up"
				            primary={true}
				            style={styles.button}
				            //onClick={}
	        			/>
        			</Link>
        			<br/>
        			<TextField
			            hintText="Enter your email"
			            floatingLabelText="E-mail"
			            type="email"
			            id="email"
			            value={email}
			            onChange={this.changeHandler}
		        	/>
		        	<br/>
		        	<TextField
				        hintText="Password"
				        floatingLabelText="Password"
				        type="password"
				        id="password"
				        value={password}
				        onChange={this.changeHandler}
				    />
				    <br/>
				    <RaisedButton
			            label="Submit"
			            primary={true}
			            style={styles.button}
			            //onClick={() => handleSubmitLogin}
			        />
				</React.Fragment>
			</MuiThemeProvider>
		)
	}
}

export default Login
