import React, { Component } from "react";
import MuiThemeProvider from "material-ui/styles/MuiThemeProvider";
import TextField from "material-ui/TextField";
import RaisedButton from "material-ui/RaisedButton";

const styles = {
  button: {
    margin: 15,
  },
};

class LoginForm extends Component {
  changeStep = (e) => {
    this.props.nextStep();
  };
  render() {
    const { handleChange, nextStep, handleSubmitLogin } = this.props;
    return (
      <MuiThemeProvider>
        <React.Fragment>
          <br />

          <RaisedButton label="log-In" primary={false} style={styles.button} />
          <RaisedButton
            label="Sign-Up"
            primary={true}
            style={styles.button}
            onClick={nextStep}
          />
          <br />
          <TextField
            hintText="Enter your email"
            floatingLabelText="E-mail"
            type="email"
            id="email"
            onChange={handleChange}
          />
          <br />
          <TextField
            hintText="Password"
            floatingLabelText="Password"
            id="password"
            onChange={handleChange}
            type="password"
          />
          <br />
          <RaisedButton
            label="Submit"
            primary={true}
            style={styles.button}
            onClick={() => handleSubmitLogin}
          />
        </React.Fragment>
      </MuiThemeProvider>
    );
  }
}

export default LoginForm;
