import React from 'react';
import axios from 'axios';
import {Link,Redirect} from 'react-router-dom';



class Login extends React.Component {
	constructor(props) {
		super(props)

		this.state = {
			context: {},
			username: "",
			password: "",
			token: "",
			error: "",
			authenticated: false

		}
	}
	componentDidMount = ()=>{
		axios.get('http://localhost:8000/login/')
		.then(
			(response)=>{
				this.setState({context:response.data})
				console.log(this.state.context)
			}
		)
		.catch(
			(error)=>{
				this.setState({error:error.message})
				console.log(this.state.error)
			}
		)

	}

	changeHandler = (e)=>{
		this.setState({
			[e.target.name]: e.target.value
		})
	}

	submitHandler = (e)=>{
		e.preventDefault()
		let form={}
		form.username=this.state.username
		form.password=this.state.password
		axios.post('http://localhost:8000/login/',form)
		.then(
			(response)=>{
				this.setState({
					token: response.data.token,
					authenticated: true
				})
			}
		)
		.catch(
			(error)=>{
				this.setState({
					error:error.message,
					authenticated: false
				})

			}
		)

	}

	render() {
		let {username,password,authenticated}=this.state
		if(authenticated){
			localStorage.setItem('token',this.state.token)
			return <Redirect to="/" />
		}
		return (
			<React.Fragment>
				<center>
					<h4>Login Form</h4>
				</center>
				<br/>
				<form className="form-group" onSubmit={this.submitHandler}>
					<div className="row">
						<div className="col" />
						<input 
							name="username" 
							type="text" 
							className="form-control col"
							onChange={this.changeHandler}
							value={username}
							placeholder="Username"
						/>
						<div className="col" />
					</div>
					<div className="row">
						<div className="col" />
						<input 
							name="password" 
							type="password"
							className="form-control col"
							onChange={this.changeHandler}
							value={password}
							placeholder="Password"
						/>
						<div className="col" />
					</div>
					<div className="row">
						<div className="col" />
						<button className="btn btn-primary col" type="submit">
							Login
						</button>
						<div className="col" />
					</div>
					<div className="row">
						<div className="col" />
						<Link to="/register" className="col">
							<button className="btn btn-primary">Regiter</button>
						</Link>
						<div className="col" />
					</div>
				</form>
			</React.Fragment>
		)
	}
}

export default Login
