import React from 'react'
import MuiThemeProvider from "material-ui/styles/MuiThemeProvider";
import TextField from "material-ui/TextField";
import RaisedButton from "material-ui/RaisedButton";

const styles = {
	button: {
		margin: 15,
	},
};

export class SignUp extends React.Component {
	constructor(props) {
		super(props)

		this.state = {
			 context: {},
			 email: "",
			 firstname: "",
			 lastname: "",
			 "password1": "",
			 "password2": "",

		}
	}

	changeHandler = (e)=>{
		this.setState({
			[e.target.name]: e.target.value
		})
	}

	render() {
		let {email,firstname,lastname,password1,password2} = this.state
		return (
			<MuiThemeProvider>
				<React.Fragment>
					<br/>
					<RaisedButton
				        label="Log-In"
				        primary={true}
				        style={styles.button}
				        //onClick={prevStep}
				    />
          			<RaisedButton 
          				label="Sign-Up" 
          				primary={false} 
          				style={styles.button} 
      				/>
      				<br/>
      				<TextField
			            hintText="Enter your firstname"
			            floatingLabelText="First Name"
			            id="firstname"
			            value={firstname}
			            onChange={this.changeHandler}
			        />
			        <br/>
			        <TextField
				        hintText="Enter your lastname"
				        floatingLabelText="Last Name"
				        id="lastname"
				        value={lastname}
				        onChange={this.changeHandler}
          			/>
          			<br/>
          			<TextField
			            hintText="Enter your email"
			            floatingLabelText="E-mail"
			            type="email"
			            id="email"
			            value={email}
			            onChange={this.changeHandler}
			        />
			        <br/>
			        <TextField
			            hintText="Password"
			            floatingLabelText="Password"
			            type="password"
			            id="password1"
			            value={password1}
			            onChange={this.changeHandler}
		        	/>
		            <br/>
			        <TextField
			            hintText="Confirm password"
			            floatingLabelText="Confirm-Password"
			            type="password"
			            id="password2"
			            value={password2}
			            onChange={this.changeHandler}
			        />
					<br />
        			<RaisedButton
			            label="Submit"
			            primary={true}
			            style={styles.button}
			            //onClick={handleSubmitSignup}
			            type="submit"
            		/>
				</React.Fragment>
			</MuiThemeProvider>
		)
	}
}

export default SignUp
