import React, { Component } from "react";
import LoginForm from "./login";
import SignupForm from "./signup";
import VerifyEmail from "./verifyemail";
import axios from "axios";
import HomePage from "./HomePage";
import Navbar from "./Navbar";

export default class Router extends Component {
  state = {
    step: 0,
    firstname: "",
    lastname: "",
    email: "",
    password: "",
    otpRecieved: "",
    otpSent: "",
  };
  // Proceed to next step
  nextStep = () => {
    const { step } = this.state;
    this.setState({
      step: step + 1,
    });
  };

  // Go back to prev step
  prevStep = () => {
    const { step } = this.state;
    this.setState({
      step: step - 1,
    });
  };
  handleChange = (e) => {
    this.setState({ [e.target.id]: e.target.value });
  };
  handleSubmitSignup = (e) => {
    axios({
      method: "POST",
      data: {
        username: this.state.email,
        first_name: this.state.firstname,
        last_name: this.state.lastname,
        email: this.state.email,
        password: this.state.password,
      },
      url: "http://127.0.0.1:8000/api/sign-up/",
    }).then((response) => {
      console.log(response.data);
      this.setState({
        otpRecieved: response.data.otp,
      });
    });
  };

  handleSubmitLogin = (e) => {
    console.log("success")
    axios({
      method: "POST",
      headers: {
        "Access-Control-Allow-Origin": "*",
        "Content-Type": "application/json",
      },
      data: {
        username: this.state.email,
        password: this.state.password,
      },lastName,
      url: "http://127.0.0.1:8000/api/login/",
    }).then((response) => {
      document.cookie = `usertoken=${response.data.token}`;
      this.nextStep();
    });
  };

  checkMail = (e) => {
    console.log(this.state);
    if (this.state.otpSent === this.state.otpRecieved) {
      this.setState({
        step: 0,
      });
    } else {
      this.setState({
        step: 2,
      });
    }
  };
  render() {
    const { firstName, lastName, email, password } = this.state;
    const values = { firstName, lastName, email, password };
    const { step } = this.state;
    switch (step) {
      case 3:
        return (
          <VerifyEmail
            handleChange={this.handleChange}
            checkMail={this.checkMail}
          />
        );
      case 2:
        return (
          <SignupForm
            prevStep={this.prevStep}
            handleChange={this.handleChange}
            values={values}
            handleSubmitSignup={this.handleSubmitSignup}
          />
        );
      case 1:
        return (
          <LoginForm
            nextStep={this.nextStep}
            handleChange={this.handleChange}
            handleSubmitLogin={this.handleSubmitLogin}
          />
        );
      default:
        return <HomePage />;
    }
  }
}
