import React from 'react'
import MuiThemeProvider from "material-ui/styles/MuiThemeProvider";
import TextField from "material-ui/TextField";
import RaisedButton from "material-ui/RaisedButton";
import {Link,Redirect} from 'react-router-dom';

const styles = {
	button: {
		margin: 15,
	},
};

export class SignUp extends React.Component {
	constructor(props) {
		super(props)

		this.state = {
			 context: {},
			 email: "",
			 firstname: "",
			 lastname: "",
			 "password1": "",
			 "password2": "",

		}
	}

	changeHandler = (e)=>{
		this.setState({
			[e.target.id]: e.target.value
		})
	}

	componentDidMount = ()=>{
		console.log("hi")
	}

	submitHanler = (e)=>{
		e.preventDefault()
		let form={}
		form.email = this.state.email
		form.first_name = this.state.firstname
		form.last_name = this.state.lastname
		form.password = this.state.password1
	}

	render() {
		let {email,firstname,lastname,password1,password2} = this.state
		return (
			<MuiThemeProvider>
				<React.Fragment>
					<br/>
					<Link to="/login">
						<RaisedButton
					        label="Log-In"
					        primary={true}
					        style={styles.button}
					    />
				    </Link>
				    <Link to="/signup">
	          			<RaisedButton 
	          				label="Sign-Up" 
	          				primary={false} 
	          				style={styles.button} 
	      				/>
      				</Link>
      				<br/>
      				<TextField
			            hintText="Enter your firstname"
			            floatingLabelText="First Name"
			            id="firstname"
			            value={firstname}
			            onChange={this.changeHandler}
			        />
			        <br/>
			        <TextField
				        hintText="Enter your lastname"
				        floatingLabelText="Last Name"
				        id="lastname"
				        value={lastname}
				        onChange={this.changeHandler}
          			/>
          			<br/>
          			<TextField
			            hintText="Enter your email"
			            floatingLabelText="E-mail"
			            type="email"
			            id="email"
			            value={email}
			            onChange={this.changeHandler}
			        />
			        <br/>
			        <TextField
			            hintText="Password"
			            floatingLabelText="Password"
			            type="password"
			            id="password1"
			            value={password1}
			            onChange={this.changeHandler}
		        	/>
		            <br/>
			        <TextField
			            hintText="Confirm password"
			            floatingLabelText="Confirm-Password"
			            type="password"
			            id="password2"
			            value={password2}
			            onChange={this.changeHandler}
			        />
					<br />
        			<RaisedButton
			            label="Submit"
			            primary={true}
			            style={styles.button}
			            //onClick={handleSubmitSignup}
			            type="submit"
            		/>
				</React.Fragment>
			</MuiThemeProvider>
		)
	}
}

export default SignUp
