import React from 'react';
import axios from 'axios';
import {Link,Redirect} from 'react-router-dom';

class Register extends React.Component {
	constructor(props) {
		super(props)

		this.state = {
			 context: {},
			 email: "",
			 username: "",
			 password1: "",
			 password2: "",
			 error: "",
			 token: ""
		}
	}

	componentDidMount = ()=>{
		axios.get('http://localhost:8000/register/')
		.then(
			(response)=>{
				this.setState({context: response.data})
				console.log(this.state.context)
			}
		)
		.catch(
			(error)=>{
				this.setState({error: error.message})
				console.log(this.state.error)
			}
		)

	}

	changeHandler = (e)=>{
		this.setState({
			[e.target.name]: e.target.value
		})
	}

	submitHandler = (e)=>{
		// e.preventDefault()
		let form={}
		form.email=this.state.email
		form.username=this.state.username
		form.password1=this.state.password1
		form.password2=this.state.password2

		axios.post('http://localhost:8000/register/',form)
		.then(
			(response)=>{
				this.setState({
					token: response.data.token,
					context: response.data.status
				})
				console.log(this.state.context)
			}
		)
		.catch(
			(error)=>{
				this.setState({error:error.message})
				console.log(this.state.error)
			}
		)

	}

	render() {
		let {username,email,password1,password2} = this.state
		return (
			<React.Fragment>
				<center>
					<h4>Registeration Form</h4>
				</center>
				<br/>
				<form className="form-group" onSubmit={this.submitHandler}>
					<div className="row">
						<div className="col" />
						<input 
							name="email" 
							type="email" 
							className="form-control col"
							onChange={this.changeHandler}
							value={email}
							placeholder="E-mail"
						/>
						<div className="col" />
					</div>
					<div className="row">
						<div className="col" />
						<input 
							name="username" 
							type="text" 
							className="form-control col"
							onChange={this.changeHandler}
							value={username}
							placeholder="Username"
						/>
						<div className="col" />
					</div>
					<div className="row">
						<div className="col" />
						<input 
							name="password1" 
							type="password"
							className="form-control col"
							onChange={this.changeHandler}
							value={password1}
							placeholder="Password"
						/>
						<div className="col" />
					</div>
					<div className="row">
						<div className="col" />
						<input 
							name="password2" 
							type="password"
							className="form-control col"
							onChange={this.changeHandler}
							value={password2}
							placeholder="Confirm Password"
						/>
						<div className="col" />
					</div>
					<div className="row">
						<div className="col" />
						<button className="btn btn-primary col" type="submit">
							Login
						</button>
						<div className="col" />
					</div>
				</form>
			</React.Fragment>
		)
	}
}

export default Register
