import React from 'react'
import {Link,Redirect} from 'react-router-dom'
import axios from 'axios'

const authAxios=axios.create({
	headers: {
		Authorization: `Token ${localStorage.getItem('token')}`
	}
});

class Home extends React.Component {
	constructor(props) {
		super(props)

		this.state = {
			context:{},
			error:'',
			authenticated: false
		}
	}

	componentDidMount = ()=>{
		authAxios.get('http://localhost:8000/')
		.then(
			(response)=>{
				this.setState({
					context:response.data,
					authenticated: true
				})
			}
		)

		.catch(
			(error)=>{
				this.setState({
					error:error.message,
					authenticated: false
				})
			}
		)
	}
	componentDidUpdate=() =>{
				authAxios.get('http://localhost:8000/')
		.then(
			(response)=>{
				this.setState({
					context:response.data,
					authenticated: true
				})
			}
		)

		.catch(
			(error)=>{
				this.setState({
					error:error.message,
					authenticated: false
				})
			}
		)
	}
	render() {
		let {context,error,authenticated}=this.state
		if (!authenticated){
			return(
				<React.Fragment>
					{error}<br/>
					<Link to="/login">
						Try Login Again
					</Link>
				</React.Fragment>
			)
		}
		return (
			<React.Fragment>
				Title: {context.title}
				<br/>
				Message: {context.message}
			</React.Fragment>
		)
	}
}

export default Home
